import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { User } from '../../models/user.model';

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './dashboard.component.html',
  styleUrl: './dashboard.component.scss'
})
export class DashboardComponent implements OnInit {
  user: User | null = null;

  ngOnInit() {
    // Simulazione utente loggato come admin
    // TODO: Rimuovere quando il backend gestir√† l'autenticazione
    this.user = {
      email: 'admin@medsafe.local',
      fullName: 'Admin Test',
      role: 'ADMIN',
      genere: 'NON_SPECIFICATO',
      specializzazione: 'NESSUNA',
      createdAt: '2024-01-15T10:00:00'
    };
  }

  getDoctorEmoji(): string {
    if (!this.user?.genere) return 'ü©∫';
    switch(this.user.genere) {
      case 'MASCHIO': return 'üë®‚Äç‚öïÔ∏è';
      case 'FEMMINA': return 'üë©‚Äç‚öïÔ∏è';
      case 'NON_SPECIFICATO': return 'ü©∫';
      default: return 'ü©∫';
    }
  }

  getDoctorTitle(): string {
    if (!this.user?.genere) return 'Dr.';
    switch(this.user.genere) {
      case 'MASCHIO': return 'Dr.';
      case 'FEMMINA': return 'Dott.ssa';
      case 'NON_SPECIFICATO': return 'Dr.';
      default: return 'Dr.';
    }
  }

  cards = [
    {
      title: 'Nuovo Referto',
      description: 'Carica un nuovo referto medico con i relativi file',
      icon: 'üìã',
      route: '/upload',
      color: 'primary'
    },
    {
      title: 'I miei Referti',
      description: 'Modifica o elimina un referto esistente',
      icon: '‚úèÔ∏è',
      route: '/edit',
      color: 'info'
    },
    {
      title: 'Cerca Referti',
      description: 'Cerca e visualizza referti per codice fiscale o tipo esame',
      icon: 'üîç',
      route: '/referti',
      color: 'info'
    }
  ];
}
<div class="dashboard-container">
  <header class="dashboard-header">
    <div class="header-title">
      <img src="assets/logo.png" alt="MedSafe Logo" class="dashboard-logo">
      <h1>MedSafe Dashboard</h1>
    </div>
    <div *ngIf="user" class="welcome-message">
      <span class="welcome-icon">{{ getDoctorEmoji() }}</span>
      <span class="welcome-text">
        Benvenuto, <span class="doctor-name">{{ getDoctorTitle() }} {{ user.fullName }}</span>
      </span>
    </div>
  </header>

  <div class="dashboard-grid">
    <a *ngFor="let card of cards" 
       [routerLink]="card.route" 
       class="dashboard-card"
       [attr.data-color]="card.color">
      <div class="card-icon">{{ card.icon }}</div>
      <h2 class="card-title">{{ card.title }}</h2>
      <p class="card-description">{{ card.description }}</p>
      <span class="card-arrow">‚Üí</span>
    </a>
  </div>
</div>
