import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { User } from '../../models/user.model';
import { UserService } from '../../services/user.service';

@Component({
  selector: 'app-user-profile',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './user-profile.component.html',
  styleUrl: './user-profile.component.scss'
})
export class UserProfileComponent implements OnInit {
  user: User | null = null;
  loading = true;
  error: string | null = null;
  successMessage: string | null = null;
  isEditMode = false;
  
  // Dati per l'edit
  editData = {
    genere: '',
    specializzazione: ''
  };

  // Liste per dropdown
  generi: any[] = [];
  specializzazioni: any[] = [];
  filteredSpecializzazioni: any[] = [];
  showSpecializzazioniDropdown = false;

  constructor(private userService: UserService) {}

  ngOnInit() {
    this.loadUserProfile();
    this.loadGeneri();
    this.loadSpecializzazioni();
  }

  loadUserProfile() {
    // Simulazione utente loggato come admin
    // TODO: Rimuovere quando il backend gestirà l'autenticazione
    this.user = {
      email: 'admin@medsafe.local',
      fullName: 'Admin Test',
      role: 'ADMIN',
      genere: 'NON_SPECIFICATO',
      specializzazione: 'NESSUNA',
      createdAt: '2024-01-15T10:00:00'
    };
    this.loading = false;

    // Codice originale da riabilitare quando il backend è pronto:
    /*
    this.userService.getCurrentUser().subscribe({
      next: (user) => {
        this.user = user;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Errore nel caricamento del profilo';
        this.loading = false;
        console.error('Errore caricamento profilo:', err);
      }
    });
    */
  }

  loadGeneri() {
    this.userService.getGeneri().subscribe({
      next: (generi) => {
        this.generi = generi;
      },
      error: (err) => {
        console.error('Errore caricamento generi:', err);
      }
    });
  }

  loadSpecializzazioni() {
    this.userService.getSpecializzazioni().subscribe({
      next: (specializzazioni) => {
        this.specializzazioni = specializzazioni;
        this.filteredSpecializzazioni = specializzazioni;
      },
      error: (err) => {
        console.error('Errore caricamento specializzazioni:', err);
      }
    });
  }

  filterSpecializzazioni() {
    const searchTerm = this.editData.specializzazione.toLowerCase();
    this.filteredSpecializzazioni = this.specializzazioni.filter(spec =>
      spec.toLowerCase().startsWith(searchTerm)
    );
    this.showSpecializzazioniDropdown = true;
  }

  selectSpecializzazione(spec: string) {
    this.editData.specializzazione = spec;
    this.showSpecializzazioniDropdown = false;
  }

  onSpecializzazioneFocus() {
    this.filteredSpecializzazioni = this.specializzazioni;
    this.showSpecializzazioniDropdown = true;
  }

  onSpecializzazioneBlur() {
    // Ritarda la chiusura per permettere il click su un'opzione
    setTimeout(() => {
      this.showSpecializzazioniDropdown = false;
    }, 200);
  }

  isFormValid(): boolean {
    return this.editData.genere.trim() !== '' && 
           this.editData.specializzazione.trim() !== '';
  }

  getValidationError(): string | null {
    if (this.editData.genere.trim() === '') {
      return 'Il genere è obbligatorio';
    }
    if (this.editData.specializzazione.trim() === '') {
      return 'La specializzazione è obbligatoria';
    }
    return null;
  }

  enableEditMode() {
    this.isEditMode = true;
    this.editData.genere = this.user?.genere || 'NON_SPECIFICATO';
    this.editData.specializzazione = this.user?.specializzazione || '';
    this.error = null;
    this.successMessage = null;
  }

  cancelEdit() {
    this.isEditMode = false;
    this.error = null;
    this.successMessage = null;
  }

  saveProfile() {
    // Validazione
    const validationError = this.getValidationError();
    if (validationError) {
      this.error = validationError;
      return;
    }

    this.loading = true;
    this.error = null;
    this.successMessage = null;

    this.userService.updateProfile(this.editData.genere, this.editData.specializzazione).subscribe({
      next: (updatedUser) => {
        this.user = updatedUser;
        this.isEditMode = false;
        this.loading = false;
        this.successMessage = 'Profilo aggiornato con successo!';
        setTimeout(() => this.successMessage = null, 3000);
      },
      error: (err) => {
        this.error = 'Errore nell\'aggiornamento del profilo';
        this.loading = false;
        console.error('Errore aggiornamento profilo:', err);
      }
    });
  }

  getGenereLabel(genere?: string): string {
    if (!genere) return 'Non specificato';
    switch(genere) {
      case 'MASCHIO': return 'Maschio';
      case 'FEMMINA': return 'Femmina';
      case 'NON_SPECIFICATO': return 'Non specificato';
      default: return 'Non specificato';
    }
  }
}
<div class="profile-container">
  <div class="profile-header">
    <h2>Il Mio Profilo</h2>
    <button *ngIf="!isEditMode && user" class="btn-edit" (click)="enableEditMode()">
      ✏️ Modifica Profilo
    </button>
  </div>

  <div *ngIf="loading" class="loading">
    <p>Caricamento profilo...</p>
  </div>

  <div *ngIf="error" class="error">
    <p>{{ error }}</p>
  </div>

  <div *ngIf="successMessage" class="success">
    <p>{{ successMessage }}</p>
  </div>

  <div *ngIf="user && !loading" class="profile-content">
    <div class="profile-card">
      <div class="profile-item">
        <label>Nome Completo</label>
        <p>{{ user.fullName }}</p>
      </div>

      <div class="profile-item">
        <label>Email</label>
        <p>{{ user.email }}</p>
      </div>

      <div class="profile-item">
        <label>Genere <span class="required">*</span></label>
        <p *ngIf="!isEditMode">{{ getGenereLabel(user.genere) }}</p>
        <select *ngIf="isEditMode" [(ngModel)]="editData.genere" class="form-select" required>
          <option value="">-- Seleziona genere --</option>
          <option *ngFor="let genere of generi" [value]="genere">
            {{ genere === 'MASCHIO' ? 'Maschio' : genere === 'FEMMINA' ? 'Femmina' : 'Non specificato' }}
          </option>
        </select>
      </div>

      <div class="profile-item">
        <label>Specializzazione <span class="required">*</span></label>
        <p *ngIf="!isEditMode">{{ user.specializzazione || 'Non specificata' }}</p>
        <div *ngIf="isEditMode" class="autocomplete-container">
          <input 
            type="text" 
            [(ngModel)]="editData.specializzazione" 
            (input)="filterSpecializzazioni()"
            (focus)="onSpecializzazioneFocus()"
            (blur)="onSpecializzazioneBlur()"
            placeholder="Cerca specializzazione..."
            class="form-input"
            required
          >
          <div *ngIf="showSpecializzazioniDropdown && filteredSpecializzazioni.length > 0" class="autocomplete-dropdown">
            <div 
              *ngFor="let spec of filteredSpecializzazioni" 
              class="autocomplete-item"
              (click)="selectSpecializzazione(spec)"
            >
              {{ spec }}
            </div>
          </div>
          <div *ngIf="showSpecializzazioniDropdown && filteredSpecializzazioni.length === 0 && editData.specializzazione.trim() !== ''" class="autocomplete-empty">
            Nessuna specializzazione trovata
          </div>
        </div>
      </div>

      <div class="profile-item">
        <label>Data Creazione Account</label>
        <p>{{ user.createdAt | date: 'dd/MM/yyyy HH:mm' }}</p>
      </div>

      <div *ngIf="isEditMode" class="profile-actions">
        <button 
          class="btn-save" 
          (click)="saveProfile()"
          [disabled]="!isFormValid() || loading"
        >
          💾 {{ loading ? 'Salvando...' : 'Salva' }}
        </button>
        <button class="btn-cancel" (click)="cancelEdit()" [disabled]="loading">❌ Annulla</button>
      </div>
    </div>
  </div>
</div>
