import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { UserService } from '../../services/user.service';
import { User } from '../../models/user.model';

@Component({
  selector: 'app-users-list',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './users-list.component.html',
  styleUrl: './users-list.component.scss'
})
export class UsersListComponent implements OnInit {
  users: User[] = [];
  loading = true;
  error: string | null = null;

  constructor(private userService: UserService) {}

  ngOnInit() {
    this.loadUsers();
  }

  loadUsers() {
    this.userService.getAllUsers().subscribe({
      next: (users) => {
        this.users = users;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Errore nel caricamento degli utenti';
        this.loading = false;
        console.error('Errore caricamento utenti:', err);
      }
    });
  }

  getRoleBadgeClass(role: string): string {
    return role === 'ADMIN' ? 'badge-admin' : 'badge-medico';
  }

  getRoleLabel(role: string): string {
    return role === 'ADMIN' ? 'Amministratore' : 'Medico';
  }

  getDoctorTitle(genere?: string): string {
    if (genere === 'FEMMINA') {
      return 'Dott.ssa';
    }
    return 'Dr.';
  }

  toggleUserStatus(user: User, event: Event): void {
    event.preventDefault(); // Previene il cambio automatico del checkbox
    
    if (!user.id) return;

    const action = user.enabled ? 'disabilitare' : 'abilitare';
    if (!confirm(`Sei sicuro di voler ${action} l'utente ${user.fullName}?`)) {
      return; // L'utente ha annullato, non fare nulla
    }

    const observable = user.enabled 
      ? this.userService.disableUser(user.id)
      : this.userService.enableUser(user.id);

    observable.subscribe({
      next: () => {
        user.enabled = !user.enabled; // Aggiorna lo stato solo dopo il successo
        console.log(`Utente ${user.fullName} ${user.enabled ? 'abilitato' : 'disabilitato'}`);
      },
      error: (err) => {
        console.error('Errore nel cambio stato utente:', err);
        alert('Errore nel cambio stato dell\'utente');
      }
    });
  }
}
<div class="users-container">
  <div class="users-header">
    <h2>Elenco Utenti</h2>
    <p class="subtitle">Gestione utenti del sistema MedSafe</p>
  </div>

  <div *ngIf="loading" class="loading">
    <p>Caricamento utenti...</p>
  </div>

  <div *ngIf="error" class="error">
    <p>{{ error }}</p>
  </div>

  <div *ngIf="!loading && users.length > 0" class="users-table-container">
    <table class="users-table">
      <thead>
        <tr>
          <th>Nome Completo</th>
          <th>Specializzazione</th>
          <th>Email</th>
          <th>Ruolo</th>
          <th>Stato</th>
          <th>Data Creazione</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let user of users">
          <td class="user-name">
            <span class="name-icon">ðŸ‘¤</span>
            {{ getDoctorTitle(user.genere) }} {{ user.fullName }}
          </td>
          <td>
            <span class="specialization">{{ user.specializzazione || 'NESSUNA' }}</span>
          </td>
          <td>{{ user.email }}</td>
          <td>
            <span class="badge" [ngClass]="getRoleBadgeClass(user.role)">
              {{ getRoleLabel(user.role) }}
            </span>
          </td>
          <td>
            <div class="status-toggle" *ngIf="user.role !== 'ADMIN'">
              <label class="switch">
                <input 
                  type="checkbox" 
                  [checked]="user.enabled !== false"
                  (click)="toggleUserStatus(user, $event)"
                >
                <span class="slider"></span>
              </label>
              <span class="status-label" [class.active]="user.enabled !== false">
                {{ user.enabled !== false ? 'âœ“ Attivo' : 'âœ– Disattivo' }}
              </span>
            </div>
            <span *ngIf="user.role === 'ADMIN'" class="badge badge-active">
              âœ“ Sempre Attivo
            </span>
          </td>
          <td>{{ user.createdAt | date: 'dd/MM/yyyy HH:mm' }}</td>
        </tr>
      </tbody>
    </table>
  </div>

  <div *ngIf="!loading && users.length === 0" class="no-users">
    <p>Nessun utente trovato</p>
  </div>
</div>
