import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RefertiService, RefertoDTO } from '../../services/referti.service';

@Component({
  selector: 'app-referti-edit',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './referti-edit.component.html',
  styleUrl: './referti-edit.component.scss'
})
export class RefertiEditComponent implements OnInit {
  // Simulazione utente loggato come admin
  // TODO: Rimuovere quando il backend gestirà l'autenticazione
  private readonly userEmail = 'admin@medsafe.local';
  
  // Results
  referti: RefertoDTO[] = [];
  selectedReferto: RefertoDTO | null = null;
  isLoading = false;
  errorMessage = '';
  successMessage = '';
  
  // Edit mode
  isEditMode = false;
  editData = {
    codiceFiscalePaziente: '',
    tipoEsame: '',
    dataEsame: '',
    nomeFile: '',
    testoReferto: '',
    conclusioni: ''
  };
  selectedFile: File | null = null;

  constructor(private refertiService: RefertiService) {}

  ngOnInit() {
    this.loadMyReferti();
  }

  loadMyReferti() {
    this.isLoading = true;
    this.errorMessage = '';
    this.successMessage = '';

    this.refertiService.getRefertiByAutoreEmail(this.userEmail).subscribe({
      next: (data) => {
        this.isLoading = false;
        this.referti = data;
        if (this.referti.length === 0) {
          this.errorMessage = 'Non hai ancora creato nessun referto';
        }
      },
      error: (error: any) => {
        this.isLoading = false;
        // Se errore 500, probabilmente non ci sono referti
        if (error.status === 500) {
          this.errorMessage = 'Non hai ancora creato nessun referto';
        } else {
          this.errorMessage = 'Errore durante il caricamento dei referti: ' + (error.message || 'Errore sconosciuto');
        }
      }
    });
  }

  selectReferto(referto: RefertoDTO) {
    this.selectedReferto = referto;
    this.isEditMode = false;
    this.successMessage = '';
    this.errorMessage = '';
  }

  enterEditMode() {
    if (!this.selectedReferto) return;
    
    this.isEditMode = true;
    this.selectedFile = null;
    this.editData = {
      codiceFiscalePaziente: this.selectedReferto.codiceFiscale,
      tipoEsame: this.selectedReferto.tipoEsame,
      dataEsame: this.selectedReferto.dataCaricamento || '',
      nomeFile: this.selectedReferto.nomeFile,
      testoReferto: this.selectedReferto.testoReferto || '',
      conclusioni: this.selectedReferto.conclusioni || ''
    };
  }

  onFileSelected(event: any) {
    const file = event.target.files[0];
    if (file) {
      this.selectedFile = file;
    }
  }

  cancelEdit() {
    this.isEditMode = false;
    this.errorMessage = '';
    this.successMessage = '';
  }

  saveEdit() {
    if (!this.selectedReferto) return;

    this.isLoading = true;
    this.errorMessage = '';
    this.successMessage = '';

    const updatedReferto: RefertoDTO = {
      ...this.selectedReferto,
      autoreEmail: this.userEmail,
      codiceFiscale: this.editData.codiceFiscalePaziente,
      tipoEsame: this.editData.tipoEsame as any,
      nomeFile: this.editData.nomeFile,
      nomePaziente: this.selectedReferto.nomePaziente,
      testoReferto: this.editData.testoReferto,
      conclusioni: this.editData.conclusioni
    };

    // TODO: Se c'è un nuovo file, inviare anche il file
    // Per ora aggiorniamo solo i dati testuali
    this.refertiService.editReferto(updatedReferto).subscribe({
      next: () => {
        this.isLoading = false;
        this.successMessage = 'Referto modificato con successo';
        this.isEditMode = false;
        // Update the selected referto with new data
        this.selectedReferto = updatedReferto;
        // Update in the list
        const index = this.referti.findIndex(r => r.id === this.selectedReferto!.id);
        if (index !== -1) {
          this.referti[index] = this.selectedReferto;
        }
      },
      error: (error: any) => {
        this.isLoading = false;
        this.errorMessage = 'Errore durante la modifica: ' + (error.message || 'Errore sconosciuto');
      }
    });
  }

  deleteReferto() {
    if (!this.selectedReferto || !this.selectedReferto.id) return;
    
    if (!confirm(`Sei sicuro di voler eliminare il referto "${this.selectedReferto.nomeFile}"?`)) {
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';
    this.successMessage = '';

    this.refertiService.removeReferto(this.selectedReferto.id).subscribe({
      next: () => {
        this.successMessage = 'Referto eliminato con successo';
        // Rimuovi il referto dalla lista locale immediatamente
        this.referti = this.referti.filter(r => r.id !== this.selectedReferto?.id);
        this.selectedReferto = null;
        this.isEditMode = false;
        this.isLoading = false;
        
        // Se la lista è vuota, mostra messaggio appropriato dopo un attimo
        if (this.referti.length === 0) {
          setTimeout(() => {
            this.successMessage = '';
            this.errorMessage = 'Non hai ancora creato nessun referto';
          }, 1500);
        }
      },
      error: (error) => {
        this.isLoading = false;
        this.errorMessage = 'Errore durante l\'eliminazione: ' + (error.message || 'Errore sconosciuto');
      }
    });
  }

  getBadgeClass(tipoEsame: string): string {
    const tipo = tipoEsame?.toUpperCase() || '';
    switch(tipo) {
      case 'RADIOGRAFIA':
      case 'Radiografia'.toUpperCase():
        return 'badge-radiografia';
      case 'ECOGRAFIA':
      case 'Ecografia'.toUpperCase():
        return 'badge-ecografia';
      case 'TAC':
        return 'badge-tac';
      case 'RISONANZA':
      case 'RISONANZA MAGNETICA':
      case 'Risonanza'.toUpperCase():
        return 'badge-risonanza';
      case 'ESAMI_LABORATORIO':
      case 'ESAMI DI LABORATORIO':
      case 'Esami_Laboratorio'.toUpperCase():
        return 'badge-laboratorio';
      default:
        return '';
    }
  }

  getImageFileName(url?: string): string {
    const fileUrl = url || this.selectedReferto?.fileUrlImmagine;
    if (!fileUrl) {
      return 'Nessun file caricato';
    }
    const parts = fileUrl.split('/');
    return parts[parts.length - 1] || 'File non disponibile';
  }

  downloadPdf(id: number): void {
    this.refertiService.downloadPdf(id).subscribe({
      next: (blob) => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `referto_${id}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      },
      error: (error) => {
        this.errorMessage = 'Errore durante il download del PDF';
        console.error('Error:', error);
      }
    });
  }

  downloadImmagine(id: number): void {
    this.refertiService.downloadImmagine(id).subscribe({
      next: (blob) => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `immagine_${id}`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      },
      error: (error) => {
        this.errorMessage = 'Errore durante il download dell\'immagine';
        console.error('Error:', error);
      }
    });
  }
}
<div class="edit-container">
  <header class="page-header">
    <h1>I miei referti</h1>
    <p>Visualizza, modifica ed elimina i tuoi referti</p>
  </header>

  <!-- Messages -->
  <div *ngIf="errorMessage" class="alert alert-error">
    {{ errorMessage }}
  </div>
  <div *ngIf="successMessage" class="alert alert-success">
    {{ successMessage }}
  </div>
  <div *ngIf="isLoading" class="loading-spinner">
    <div class="spinner"></div>
    <p>Caricamento in corso...</p>
  </div>

  <!-- Results List -->
  <div *ngIf="referti.length > 0" class="results-section">
    <h2>Risultati: {{ referti.length }}</h2>
    <div class="referti-grid">
      <div *ngFor="let referto of referti" 
           class="referto-card"
           [class.selected]="selectedReferto?.id === referto.id"
           (click)="selectReferto(referto)">
        <div class="referto-header">
          <h3>{{ referto.nomeFile }}</h3>
          <span class="badge" [ngClass]="getBadgeClass(referto.tipoEsame)">{{ referto.tipoEsame }}</span>
        </div>
        <div class="referto-body">
          <p><strong>CF Paziente:</strong> {{ referto.codiceFiscale }}</p>
          <p><strong>Data Caricamento:</strong> {{ referto.dataCaricamento | date:'dd/MM/yyyy HH:mm' }}</p>
          <p><strong>Medico:</strong> {{ referto.autoreEmail }}</p>
        </div>
      </div>
    </div>
  </div>

  <!-- Selected Referto Details -->
  <div *ngIf="selectedReferto && !isEditMode" class="details-section card">
    <h2>Dettagli Referto</h2>
    
    <div class="details-grid">
      <div class="detail-item">
        <label>Nome File:</label>
        <span>{{ selectedReferto.nomeFile }}</span>
      </div>
      <div class="detail-item">
        <label>Tipo Esame:</label>
        <span>{{ selectedReferto.tipoEsame }}</span>
      </div>
      <div class="detail-item">
        <label>Codice Fiscale Paziente:</label>
        <span>{{ selectedReferto.codiceFiscale }}</span>
      </div>
      <div class="detail-item">
        <label>Data Caricamento:</label>
        <span>{{ selectedReferto.dataCaricamento | date:'dd/MM/yyyy HH:mm' }}</span>
      </div>
      <div class="detail-item">
        <label>Email Medico:</label>
        <span>{{ selectedReferto.autoreEmail }}</span>
      </div>
    </div>

    <div class="button-group">
      <button (click)="downloadPdf(selectedReferto.id!)" class="btn btn-primary">
        <span class="btn-icon">📝</span>
        <span class="btn-text">Scarica Documento Referto</span>
      </button>
      <button (click)="downloadImmagine(selectedReferto.id!)" class="btn btn-info">
        <span class="btn-icon">🖼️</span>
        <span class="btn-text">Scarica Esame</span>
      </button>
      <button (click)="enterEditMode()" class="btn btn-accent">
        <span class="btn-icon">✏️</span>
        <span class="btn-text">Modifica</span>
      </button>
      <button (click)="deleteReferto()" class="btn btn-danger">
        🗑️ Elimina
      </button>
    </div>
  </div>

  <!-- Edit Form -->
  <div *ngIf="isEditMode" class="edit-section card">
    <h2>Modifica Referto</h2>
    
    <form class="edit-form">
      <div class="form-group">
        <label for="editNomeFile">Nome File:</label>
        <input 
          type="text" 
          id="editNomeFile"
          [(ngModel)]="editData.nomeFile"
          name="nomeFile"
          class="form-control"
          required>
      </div>

      <div class="form-group">
        <label for="editTipoEsame">Tipo Esame:</label>
        <select 
          id="editTipoEsame"
          [(ngModel)]="editData.tipoEsame"
          name="tipoEsame"
          class="form-control"
          required>
          <option value="TAC">TAC</option>
          <option value="Radiografia">Radiografia</option>
          <option value="Ecografia">Ecografia</option>
          <option value="Risonanza">Risonanza Magnetica</option>
          <option value="Esami_Laboratorio">Esami di Laboratorio</option>
        </select>
      </div>

      <div class="form-group">
        <label for="editCodiceFiscale">Codice Fiscale Paziente:</label>
        <input 
          type="text" 
          id="editCodiceFiscale"
          [(ngModel)]="editData.codiceFiscalePaziente"
          name="codiceFiscale"
          class="form-control"
          maxlength="16"
          required>
      </div>

      <div class="form-group full-width">
        <label for="editTestoReferto">Testo Referto: <span class="required">*</span></label>
        <textarea 
          id="editTestoReferto"
          [(ngModel)]="editData.testoReferto"
          name="testoReferto"
          class="form-control"
          rows="6"
          placeholder="Inserisci il testo del referto..."
          required></textarea>
      </div>

      <div class="form-group full-width">
        <label for="editConclusioni">Conclusioni: <span class="required">*</span></label>
        <textarea 
          id="editConclusioni"
          [(ngModel)]="editData.conclusioni"
          name="conclusioni"
          class="form-control"
          rows="4"
          placeholder="Inserisci le conclusioni..."
          required></textarea>
      </div>

      <div class="form-group full-width">
        <label for="editImmagine">Immagine Corrente:</label>
        <div class="current-file-info" *ngIf="selectedReferto?.fileUrlImmagine">
          <span class="file-icon">🖼️</span>
          <span class="file-name">{{ getImageFileName(selectedReferto?.fileUrlImmagine) }}</span>
          <span class="file-status">✓ Già caricato</span>
        </div>
        <label for="editImmagine" class="mt-2">Cambia Immagine (opzionale):</label>
        <input 
          type="file" 
          id="editImmagine"
          (change)="onFileSelected($event)"
          accept="image/*"
          class="form-control file-input">
        <small class="form-help">Lascia vuoto per mantenere l'immagine corrente, oppure seleziona una nuova immagine per sostituirla</small>
      </div>

      <div class="button-group">
        <button 
          type="button"
          (click)="saveEdit()" 
          [disabled]="isLoading"
          class="btn btn-primary">
          💾 Salva Modifiche
        </button>
        <button 
          type="button"
          (click)="cancelEdit()" 
          class="btn btn-secondary">
          ✖ Annulla
        </button>
      </div>
    </form>
  </div>
</div>
